import Items from './Items';
import './App.css';
import Item from './Item';
import React from 'react';
import check from './checkOut';
function goto(){

  var amt = 0;
  var idl=[];
  Items.map(function(NewItem){
      var quant=document.getElementById("quant");
    var  check=document.getElementById("check");

if(check.checked == true){
  amt = amt +(quant*NewItem.price);
idl.push(NewItem.key);

}
console.log(amt);
console.log(idl);

      });

}


function App() {

  return (
    <div className="App">
    <h1>
     Food App Menu
    </h1><br/>
    <form>
<table class="table">

  <thead class="thead-dark">
    <tr>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">Price</th>
      <th scope="col">quantity</th>
      <th scope="col">Add Item</th>

    </tr>
  </thead>
  <tbody>
{Items.map(function(NewItem){
  return (
    <Item 
    key= {NewItem.key}
    img= {NewItem.img}
    name= {NewItem.name}
    price={NewItem.price}
    />
  );
})}
</tbody>
    <tr>
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col"><button class="btn btn-success" onClick={goto}>Check Out</button></th>
      <th scope="col"></th>
      <th scope="col"></th>

    </tr>

</table>
</form>
    </div>
  );
}

export default App;
