import Items from './Items';
import './App.css';
import Item from './Item';
import React from 'react';
import ReactDOM from 'react-dom';

var idl=[];
var quantity=[];
function Check(){
  var i=0;
  return (
    <div className="App">
    <h1>
     Food App Menu
    </h1><br/>
    <form>
<table class="table">

  <thead class="thead-dark">
    <tr>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">Price</th>
      <th scope="col">quantity</th>
      <th scope="col">Amount</th>

    </tr>
  </thead>
  <tbody>
  {idl.map(function(iter){
  return (
    <tr>
    <td><img src={Items[iter].img}/></td>
    <td>{Items[iter].name}</td>
    <td>{Items[iter].price}</td>
    <td>{quantity[iter]}</td>
    <td>{Items[iter].price *quantity[iter] }</td>
    </tr>
  );
})}
</tbody>
    <tr>
      <th scope="col">Total</th>
      <th scope="col"></th>
      <th scope="col">{}</th>
      <th scope="col"></th>
      <th scope="col"><button class="btn btn-success" onClick={goto}>Check Out</button></th>

    </tr>

</table>
</form>
    </div>
  );


}
function goto(){

  var i=0;
 for(i=0;i<Items.length;i++){
var check = document.getElementById(Items[i].name);
var quant = document.getElementById(Items[i].key);

  if(check.checked === true){
    idl.push(i);
    quantity.push(quant);
  }
 }

}


function App() {

  return (
    <div className="App">
    <h1>
     Food App Menu
    </h1><br/>
    <form>
<table class="table">

  <thead class="thead-dark">
    <tr>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">Price</th>
      <th scope="col">quantity</th>
      <th scope="col">Add Item</th>

    </tr>
  </thead>
  <tbody>
{Items.map(function(NewItem){
  return (
    <Item 
    id= {NewItem.key}
    img= {NewItem.img}
    name= {NewItem.name}
    price={NewItem.price}
    />
  );
})}
</tbody>
    <tr>
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col"><button class="btn btn-success" onClick={goto}>Check Out</button></th>
      <th scope="col"></th>
      <th scope="col"></th>

    </tr>

</table>
</form>
    </div>
  );
}

export default App;
