import Items from './Items';
import './App.css';
import Item from './Item';
import React from 'react';
import check from './checkOut';


function goto(){
  const amt = 0;
  const [notes, setNotes] = useState([]);

    function addItem(NewItem){
      setItem(prevItem =>{
        return [...prevItem, newItem];
      });

    }

    function deleteNote(id){
  setNotes(prevNotes =>{
    return prevNotes.filter((noteItem,index) =>{
      return index !== id;
    });
  });
    }

    return (
      <div>
        <Header />
        <CreateArea onAdd={addNote} />
        {notes.map((noteItem, index) => {
          return <Note
          key={index}
          id={index}
           title={noteItem.title} 
           content={noteItem.content} 
           onDelete={deleteNote} />
        })}
        <Footer />
      </div>
    );
  }


function App() {

  return (
    <div className="App">
    <h1>
     Food App Menu
    </h1>
    <form>
<table class="table">

  <thead class="thead-dark">
    <tr>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">Price</th>
      <th scope="col">quantity</th>
      <th scope="col">Add Item</th>

    </tr>
  </thead>
  <tbody>
{Items.map(function(NewItem){
  return (
    <Item 
    key= {NewItem.key}
    img= {NewItem.img}
    name= {NewItem.name}
    price={NewItem.price}
    quant={document.getElementById("quant")}
    check={document.getElementById("check")}
    />
  );
})}
</tbody>
<button class="btn btn-success" onClick={goto}>Check Out</button>
</table>
</form>
    </div>
  );
}

export default App;
