import Items from './Items';
import './App.css';
import Item from './Item';
import React from 'react';
import ReactDOM from 'react-dom';

const idl=[];
const quantity=[];
var amt =0;


function Check(){
  return (
      <div className="App">
      <h1>
       Billing
      </h1><br/>
      <form>
  <table class="table">

    <thead class="thead-dark">
      <tr>
        <th scope="col"></th>
        <th scope="col">Name</th>
        <th scope="col">Price</th>
        <th scope="col">quantity</th>
        <th scope="col">Amount</th>

      </tr>
    </thead>
    <tbody>
    {Items.forEach(function(index){
      var check = document.getElementById(Items[index].name);
      var quant = document.getElementById(Items[index].key);
console.log(check);
if(check.checked === true){  
    amt = amt + Items[index].price*quant;
  }
    return (
      <tr>
      <td><img src={Items[index].img}/></td>
      <td>{Items[index].name}</td>
      <td>{Items[index].price}</td>
      <td>{quant}</td>
      <td>{Items[index].price *quant}</td>
      </tr>
    );})}
  </tbody>
      <tr>
        <th scope="col">Total</th>
        <th scope="col"></th>
        <th scope="col">{amt}</th>
        <th scope="col"></th>
        <th scope="col"><button class="btn btn-success" >Pay</button></th>

      </tr>

  </table>
  </form>
      </div>
  );

    }



function goto(){

ReactDOM.render(<Check/>, document.getElementById('root'));

}


function App() {

  return (
    <div className="App">
    <h1>
     Food App Menu
    </h1><br/>
    <form>
<table class="table">

  <thead class="thead-dark">
    <tr>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">Price</th>
      <th scope="col">quantity</th>
      <th scope="col">Add Item</th>

    </tr>
  </thead>
  <tbody>
{Items.map(function(NewItem){
  return (
    <Item 
    id= {NewItem.key}
    img= {NewItem.img}
    name= {NewItem.name}
    price={NewItem.price}
    />
  );
})}
</tbody>
    <tr>
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col"><button class="btn btn-success" onClick={goto}>Check Out</button></th>
      <th scope="col"></th>
      <th scope="col"></th>

    </tr>

</table>
</form>
    </div>
  );
}
export default App;
